import { useState } from 'react'
import './index.css'

// props 接口
interface IProps {
  placeholder: string,
  options: Array<any>
}
// 模拟下拉列表的提示数据
type MockVal  = (a: string, b: number) => string
const mockVal: MockVal = (str, repeat = 1) => {
  return str.repeat(repeat)
}

// AutoComplete组件
export default function AutoComplete(props: IProps) {

  // 下拉列表是否展示
  let [isShow, setIsShow] = useState(false)
  // input中的内容
  const [value, setValue] = useState('')
  // onblur点击的元素是否是下拉列表
  const [isList, setIsList] = useState(false)

  const {placeholder, options} = props

  // focus时
  const onFocus = () => {
    if(value){
      setIsShow(true)
    }
  }
  // blur时
  const onBlur = () => {
    setTimeout(() => {
      setIsShow(false)
      setIsList(false)
    }, 100);
    
    
  }
  // change
  const onChange = (e: any) => {
    setValue(e.target.value)
    setTimeout(() => {
      console.log('value: ', value);
    }, 1000);
    
    if(value){
      setIsShow(true)
    }
  }
  // 当下拉列表被点击
  const selectValue = (e:any) => {
    // 更新isList
    setIsList(true)
    
    // 将li的数据填到input里
    setValue(e.target.innerHTML)
  }

  return (
    <div>
      <input 
        type="text" className="auto-complete" value={value}
        placeholder={placeholder} onFocus={onFocus} 
        onBlur={onBlur} onChange={onChange}
      />
      {
        isShow ? 
          (<ul className='dropdown'>
            {
              options.map((item,index) => {
                return <li className='li' key={index} onClick={selectValue}>
                  {mockVal(value, index+1)}
                </li>
              })
            }
          </ul>) : null
      }
    </div>
  );
}
