import { useState, useEffect, useRef, CSSProperties } from 'react'
import './index.css'

// props 接口
interface IProps {
  placeholder: string,
  options: Array<any>,
  notFoundContent: React.ReactNode,
  style: CSSProperties
}

/**
 * AutoComplete组件
 * @param props 
 */
export default function AutoComplete(props: IProps) {

  const valueRef: any = useRef(null)
  // const optionRef: any = useRef(null)
  // 下拉列表是否展示
  let [isShow, setIsShow] = useState(false)
  // input中的内容
  const [value, setValue] = useState('')
  // 筛选出下拉列表中包含输入字符的部分
  const [filterOptions, setFilter] = useState<Array<string>>([])
  // blur flag
  const [isBlur, setBlur] = useState(true)

  const {placeholder, options, notFoundContent, style} = props

  useEffect(()=>{
    valueRef.current = value
    // optionRef.current = filterOptions
  }, [value])



  // focus时
  const onFocus = () => {
    if(value){
      setIsShow(true)
    }
  }
  // blur时
  const onBlur = () => {
    setTimeout(() => {
      if(isBlur) {
        setIsShow(false)
        setBlur(true)
      }
    }, 100);
  }
  // change时
  const onChange = (e: any) => {
    setValue(e.target.value)
    setTimeout(() => {
      // 获取最新的value值
      let _value = valueRef.current
      let filterArr: Array<string> = []
      if(_value){
        setFilter([])
        options.forEach(str => {
          for(let char of _value){
            // 判断options中是否包含输入的字符
            if(str.indexOf(char)>=0 && !filterArr.includes(str)){
              filterArr.push(str)
              setFilter(filterArr)
            }
          }
        })
        setIsShow(true)
      } else {
        setIsShow(false)
      }
    });
    
  }
  // 当下拉列表被点击时
  const selectValue = (e:any) => {
    setBlur(false)
    // 将li的数据填到input里
    setValue(e.target.innerHTML)
    // 页面更新后
    setTimeout(() => {
      // 获取最新的value值
      let _value = valueRef.current
      console.log('当前input：', _value);

      let filterArr: Array<string> = []
      setFilter([])
      options.forEach(str => {
        for(let char of _value){
          // 判断options中是否包含输入的字符
          if(str.indexOf(char)>=0 && !filterArr.includes(str)){
            filterArr.push(str)
            setFilter(filterArr)
          }
        }
      })
    }, 1);
      
  }

  return (
    <div>
      <input 
        type="text" className="auto-complete" value={value}
        placeholder={placeholder} onFocus={onFocus} 
        onBlur={onBlur} onChange={onChange} style={style}
      />
      {
        isShow ? 
          (filterOptions.length>0 ? (<ul className='dropdown'>
            {
              filterOptions.map((item,index) => {
                return <li className='li' key={index} onClick={selectValue}>
                  {item}
                </li>
              })
            }
          </ul>) : notFoundContent) : null
      }
      <h2>{filterOptions}</h2>
    </div>
  );
}
