import { useState } from 'react'
import './index.css'

// props 接口
interface IProps {
  placeholder: string,
  options: Array<any>
}

// AutoComplete组件
export default function AutoComplete(props: IProps) {

  // 下拉列表是否展示
  let [isShow, setIsShow] = useState(false)

  const {placeholder, options} = props

  // focus时
  const onFocus = () => {
    setIsShow(true)
  }
  // blur时
  const onBlur = () => {
    setIsShow(false)
  }

  return (
    <div>
      <input 
      type="text" className="auto-complete" 
      placeholder={placeholder} onFocus={onFocus} 
      onBlur={onBlur}
      />
      {
        isShow ? 
          (<ul>
            {
              options.map(item => {
                return <li>111</li>
              })
            }
          </ul>) : null
      }
    </div>
  );
}
