import React, { useState, useEffect, useRef, CSSProperties } from 'react'
import './index.css'

// props 接口
interface IProps {
  placeholder: string,
  options: Array<any>,
  notFoundContent: React.ReactNode,
  style: CSSProperties,
  debounceDelay: number,
  dropdownClassname: string
}

/**
 * AutoComplete组件
 * @param props 
 */
export default function AutoComplete(props: IProps) {

  const valueRef = useRef<any>(null)
  // 下拉列表是否展示
  let [isShow, setIsShow] = useState<boolean>(false)
  // input中的内容
  const [value, setValue] = useState<string>('')
  // 筛选出下拉列表中包含输入字符的部分
  const [filterOptions, setFilter] = useState<Array<string>>([])
  

  const {placeholder, options, notFoundContent, style, debounceDelay, dropdownClassname} = props

  useEffect(()=>{
    valueRef.current = value
  }, [value])

  useEffect(()=>{
    setTimeout(() => {
      const liList = document.querySelectorAll('.li')
      // 实现高亮效果
      if(filterOptions.length){
        // for(let i=0; i<filterOptions.length; i++) {
        //   // 把str中的char替换成span
        //   let highlightened = filterOptions[i].replace(
        //     new RegExp(value, 'g'),
        //     `<span style='color:red'>${value}</span>`
        //     )
        //   liList[i].innerHTML = highlightened
        // }
        console.log(liList[0], filterOptions);
        // 匹配关键词
        console.log('关键词',value);
        let transformString = value.replace(/[.[*?+^$|()/]|\]|\\/g, '\\$&');
        let pattern = new RegExp(transformString, 'i'); // 这里不区分大小写
        console.log('node:', liList[0].firstChild);
        
      }
    });
  }, [filterOptions])

  /**
   *  高亮关键字
   * @param node - 节点
   * @param pattern - 用于匹配的正则表达式
   */
  type IHighlight = (node: HTMLLIElement, pattern: RegExp) => void
  const highlightKeyword: IHighlight = (node, pattern) => {
    
    // nodeType等于3表示是文本节点
    if (node.nodeType === 3) {
        // node.data为文本节点的文本内容
        var matchResult = node.data.match(pattern);
        // 有匹配上的话
        if (matchResult) {
            // 创建一个span节点，用来包裹住匹配到的关键词内容
            var highlightEl = document.createElement('span');
            // 不用类名来控制高亮，用自定义属性data-*来标识，
            // 比用类名更减少概率与原本内容重名，避免样式覆盖
            highlightEl.dataset.highlight = 'yes';
            // splitText相关知识下面再说，可以先去理解了再回来这里看
            // 从匹配到的初始位置开始截断到原本节点末尾，产生新的文本节点
            var matchNode = node.splitText(matchResult.index);
            // 从新的文本节点中再次截断，按照匹配到的关键词的长度开始截断，
            // 此时0-length之间的文本作为matchNode的文本内容
            matchNode.splitText(matchResult[0].length);
            // 对matchNode这个文本节点的内容（即匹配到的关键词内容）创建出一个新的文本节点出来
            var highlightTextNode = document.createTextNode(matchNode.data);
            // 插入到创建的span节点中
            highlightEl.appendChild(highlightTextNode);
            // 把原本matchNode这个节点替换成用于标记高亮的span节点
            matchNode.parentNode.replaceChild(highlightEl, matchNode);
        }
    } 
    // 如果是元素节点 且 不是script、style元素 且 不是已经标记过高亮的元素
    // 至于要区分什么元素里的内容不是你想要高亮的，可自己补充，这里的script和style是最基础的了
    // 不是已经标记过高亮的元素作为条件之一的理由是，避免进入死循环，一直往里套span标签
    else if ((node.nodeType === 1)  && !(/script|style/.test(node.tagName.toLowerCase())) && (node.dataset.highlight !== 'yes')) {
        // 遍历该节点的所有子孙节点，找出文本节点进行高亮标记
        var childNodes = node.childNodes;
        for (var i = 0; i < childNodes.length; i++) {
            highlightKeyword(childNodes[i], pattern);
        }
    }
  }

  // 防抖中要触发的事件
  type Event = () => void
  const event: Event = ()=>{
    console.log('event');
    setTimeout(() => {
      // 获取最新的value值
      let _value = valueRef.current
      let filterArr: Array<string> = []
      if(_value){
        setFilter([])
        options.forEach(str => {
          // 如果str以input里的值开头
          if(str.startsWith(_value)){
            filterArr.push(str)
          }
        })
        // options.forEach(str => {
        //   for(let char of _value){
        //     // 判断options中是否包含输入的字符
        //     if(str.indexOf(char)>=0 && !filterArr.includes(str)){
        //       filterArr.push(str)
        //     }
        //   }
        // })
        // 实现高亮效果
        // for(let i=0; i<filterArr.length; i++) {
        //   for(let char of _value){
        //     // 把str中的char替换成span
        //     filterArr[i] = filterArr[i].replace(
        //       new RegExp(char, 'g'),
        //       '<span>$&</span>'
        //       // '###'
        //       )
        //   }
        // }
        // console.log(filterArr);
        
        setFilter(filterArr)
        setIsShow(true)
      } else {
        setIsShow(false)
      }
    });
  }
  // debounce 防抖  ### 失败！！！###
  function debounce(delay: number, event: Event) {
    // ts里定时器相关：https://blog.csdn.net/ollin2012/article/details/88963553
    let timer:number | null = null
    return function () {
      if(timer) {
        window.clearTimeout(timer as number);
      }
      timer = window.setTimeout(() => {
        event()
      }, delay);
    }
  }


  // focus时
  const onFocus = () => {
    if(value){
      setIsShow(true)
    }
  }
  // blur时
  const onBlur = () => {
    setTimeout(() => {
      // if(isBlur) {
        setIsShow(false)
        // setBlur(true)
      // }
    }, 100);
  }
  // change时
  const onChange = (e: React.FormEvent<HTMLInputElement>): void => {
    setValue(e.currentTarget.value)
    debounce(debounceDelay, event)()
  }
  // 当下拉列表被点击时
  const selectValue = (e:any) => {
    // 将li的数据填到input里
    console.log(e.target.innerHTML);
    
    setValue(e.target.innerHTML.innerText)

    // 页面更新后
    setTimeout(() => {
      // 获取最新的value值
      let _value = valueRef.current
      console.log('当前input：', _value);

      let filterArr: Array<string> = []
      setFilter([])
      options.forEach(str => {
        for(let char of _value){
          // 判断options中是否包含输入的字符
          if(str.indexOf(char)>=0 && !filterArr.includes(str)){
            filterArr.push(str)
            setFilter(filterArr)
          }
        }
      })
    }, 1);
      
  }

  return (
    <div>
      <input 
        type="text" className="auto-complete" value={value}
        placeholder={placeholder} onFocus={onFocus} 
        onBlur={onBlur} onChange={onChange} style={style}
      />
      {
        isShow ? 
          (filterOptions.length>0 ? (<ul className={`dropdown ${dropdownClassname}`}>
            {
              filterOptions.map((item,index) => {
                return <li className='li' key={index} onClick={selectValue}>
                  {item}
                </li>
              })
            }
          </ul>) : notFoundContent) : null
      }
      <h2>{filterOptions}</h2>
    </div>
  );
}
