import { useState, useEffect, useRef } from 'react'
import './index.css'

// props 接口
interface IProps {
  placeholder: string,
  options: Array<any>
}

/**
 * AutoComplete组件
 * @param props 
 */
export default function AutoComplete(props: IProps) {

  useEffect(()=>{
    valueRef.current = value
    // optionRef.current = filterOptions
  })

  const valueRef: any = useRef(null)
  // const optionRef: any = useRef(null)
  // 下拉列表是否展示
  let [isShow, setIsShow] = useState(false)
  // input中的内容
  const [value, setValue] = useState('')
  // onblur点击的元素是否是下拉列表
  const [isList, setIsList] = useState(false)
  // 筛选出下拉列表中包含输入字符的部分
  const [filterOptions, setFilter] = useState<Array<string>>([])

  const {placeholder, options} = props


  // focus时
  const onFocus = () => {
    if(value){
      setIsShow(true)
    }
  }
  // blur时
  const onBlur = () => {
    setTimeout(() => {
      setIsShow(false)
      setIsList(false)
    }, 100);
    
    
  }
  // change时
  const onChange = (e: any) => {
    setValue(e.target.value)
    setTimeout(() => {
      // 获取最新的value值
      let _value = valueRef.current
      // let _filterOptions = optionRef.current
      // 由_value值判断是否需要展示下拉列表
      let filterArr: Array<string> = []
      if(_value){
        setFilter([])
        options.forEach(str => {
          for(let char of _value){
            // 判断options中是否包含输入的字符
            if(str.indexOf(char)>=0 && !filterArr.includes(str)){
              filterArr.push(str)
              setFilter(filterArr)
            }
          }
        })
        
        setIsShow(true)
      } else {
        setIsShow(false)
      }
    });
    
  }
  // 当下拉列表被点击时
  const selectValue = (e:any) => {
    // 更新isList
    setIsList(true)
    
    // 将li的数据填到input里
    setValue(e.target.innerHTML)
  }

  return (
    <div>
      <input 
        type="text" className="auto-complete" value={value}
        placeholder={placeholder} onFocus={onFocus} 
        onBlur={onBlur} onChange={onChange}
      />
      {
        isShow ? 
          (<ul className='dropdown'>
            {
              filterOptions.map((item,index) => {
                return <li className='li' key={index} onClick={selectValue}>
                  {item}
                </li>
              })
            }
          </ul>) : null
      }
    </div>
  );
}
