import { useState } from 'react'
import './index.css'

// props 接口
interface IProps {
  placeholder: string,
  options: Array<any>
}

// AutoComplete组件
export default function AutoComplete(props: IProps) {

  // 下拉列表是否展示
  let [isShow, setIsShow] = useState(false)
  // input中的内容
  const [value, setValue] = useState('')

  const {placeholder, options} = props

  // focus时
  const onFocus = () => {
    setIsShow(true)
  }
  // blur时
  const onBlur = () => {
    console.log(window.event);
    
    
    // setIsShow(false)
  }
  // change
  const onChange = () => {
    console.log(111);
  }
  // 当下拉列表被点击
  const selectValue = (e:any) => {
    // isList 点击的对象是否是下拉列表
    let isList = [...e.target.classList].indexOf('li')>=0
    if(!isList) {
      setIsShow(false)
    } else {
      setValue(e.target.innerHTML)
    }
  }

  return (
    <div>
      <input 
      type="text" className="auto-complete" value={value}
      placeholder={placeholder} onFocus={onFocus} 
      onBlur={onBlur} onChange={onChange}
      />
      {
        isShow ? 
          (<ul className='dropdown'>
            {
              options.map((item,index) => {
                return <li className='li' key={index} onClick={selectValue}>111</li>
              })
            }
          </ul>) : null
      }
    </div>
  );
}
